import React, { useState } from 'react';
import './dashboard.css';
import Navbar from '../../Components/Navbar/NavBar';
import Chart from '../../components/globalAssets/Chart';

const Dashboard = () => {
  const [selectedTime, setSelectedTime] = useState('day');

  const handleTimeSelection = (e) => {
    setSelectedTime(e.target.value);
  };

  return (
    <div className="dashboard">
      <Navbar />
      <div className="content">
        <div className="column-left">
          <h2>Macro Financials</h2>
          <h3>Total Balance</h3>
          <div className="balance">
            <div className="balance-number">$12,000</div>
            <div className="balance-buttons">
              <button className="btn-request">Request</button>
              <button className="btn-pay">Pay</button>
            </div>
          </div>
        </div>
        <div className="column-right">
          <h2>Payment Statistics</h2>
          <div className="time-selection">
            <label htmlFor="time-select">Select time:</label>
            <select
              id="time-select"
              value={selectedTime}
              onChange={handleTimeSelection}
            >
              <option value="day">Day</option>
              <option value="week">Week</option>
              <option value="month">Month</option>
            </select>
          </div>
          <div className="chart">
            <Chart timePeriod={selectedTime} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
